mapper.insert:
INSERT INTO empMapping (
    orga_no, emp_no, emp_rank, hiredate
)
VALUES (
    #{orgaNo}, #{empNo}, #{empRank}, #{hiredate}
)

mapper.update:
UPDATE emp SET
    emp_name = #{empName}, password = #{password}, gender = #{gender},
    emp_phone = #{empPhone}, emp_address = #{empAddress}, emp_profile = #{empProfile},
    email = #{email}, last_retiredate = #{lastRetiredate}
WHERE emp_no = #{empNo}
