mapper.insert:
INSERT INTO tbl_orga (
    upper_orga_no, orga_type, reg_user
)
VALUES (
    (
        SELECT tbl_dept_mapping.orga_no
        FROM tbl_dept_mapping
        JOIN tbl_dept td ON td.dept_no = tbl_dept_mapping.dept_no
        WHERE td.dept_no = #{deptNo}
    ),
    'emp', #{regUser}
)

    SELECT LAST_INSERT_ID()

mapper.update:
UPDATE tbl_emp SET
    emp_name = #{empName}, gender = #{gender},
    emp_phone = #{empPhone}, emp_address = #{empAddress}, emp_profile = #{empProfile},
    email = #{email}, last_retired_date = #{lastRetiredDate}
WHERE emp_no = #{empNo}
