mapper.delete:
delete from tbl_menu_role
where role_group_no=#{roleGroupNo} and menu_no in (
    with recursive menuAll as (
        select m1.menu_name, m1.menu_no, m1.upper_menu_no
        from tbl_menu m1
        where m1.menu_code = #{moduleCode}
        union all
        select m3.menu_name, m3.menu_no, m3.upper_menu_no
        from menuAll m2
                 join tbl_menu m3 on m3.upper_menu_no = m2.menu_no
    )
    select ma.menu_no
    from menuAll ma
);

select *
from tbl_menu_role;
